import React from 'react';
import { View, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Header from '../Layout/Header';
import Footer from '../Layout/Footer';
import CategorySlider from '../component/screens/categoryslider';
import Banner from '../component/screens/Banner';

const Home = () => {
  const navigation = useNavigation();

  const handleNotificationIconPress = () => {
    navigation.navigate('Notifications');
  };

  const handleWishlistIconPress = () => {
    navigation.navigate('Wishlist');
  };

  const handleCartIconPress = () => {
    navigation.navigate('Cart'); 
  };

  return (
    <View style={styles.container}>
      <Header 
        handleNotificationIconPress={handleNotificationIconPress} 
        handleWishlistIconPress={handleWishlistIconPress} 
        handleCartIconPress={handleCartIconPress}
      />
      <CategorySlider />
      <Banner text="Welcome to Our Store! Explore our wide range of products."/>
      <View style={styles.marqueeContainer}>
        <Text style={styles.marqueeText}>This is a marquee text. This is a marquee text. This is a marquee text. This is a marquee text. This is a marquee text. This is a marquee text. This is a marquee text. This is a marquee text.</Text>
      </View>
      {/* Your additional content goes here */}
      <View style={styles.content}>
        <Text style={styles.sectionHeading}>Special Deals</Text>
        {/* Add your special deals here */}
        <Text style={styles.sectionHeading}>Recently Viewed</Text>
        {/* Add your recently viewed products here */}
        <Text style={styles.sectionHeading}>Recommended Products</Text>
        {/* Add your recommended products here */}
        <Text style={styles.sectionHeading}>Product Listings</Text>
        {/* Add your product listings here */}
      </View>
      <Footer />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-between',
  },
  marqueeContainer: {
    backgroundColor: 'lightgray',
    paddingVertical: 10,
  },
  marqueeText: {
    fontSize: 16,
    textAlign: 'center',
    color: 'darkblue',
  },
  content: {
    flex: 1,
    paddingHorizontal: 10,
    paddingTop: 10,
  },
  sectionHeading: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
});

export default Home;
