// Home.js

import React, { useState, useEffect } from 'react';
import { View, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native'; // Import useNavigation hook
import Header from '../Layout/Header';
import Footer from '../Layout/Footer';
import CategorySlider from '../component/screens/categoryslider'; // Import the CategorySlider component
import Banner from '../component/screens/Banner';

const Home = () => {
  const navigation = useNavigation(); // Initialize navigation
  const [specialDeals, setSpecialDeals] = useState([]);
  const [recommendedProducts, setRecommendedProducts] = useState([]);
  const [productListings, setProductListings] = useState([]);

  useEffect(() => {
    // Fetch special deals from backend
    fetchSpecialDeals();

    // Fetch recommended products from backend
    fetchRecommendedProducts();

    // Fetch product listings from backend
    fetchProductListings();
  }, []);

  const fetchSpecialDeals = async () => {
    try {
      // Make API call to fetch special deals
      const response = await fetch('YOUR_BACKEND_API_ENDPOINT_FOR_SPECIAL_DEALS');
      const data = await response.json();
      setSpecialDeals(data);
    } catch (error) {
      console.error('Error fetching special deals:', error);
    }
  };

  const fetchRecommendedProducts = async () => {
    try {
      // Make API call to fetch recommended products
      const response = await fetch('YOUR_BACKEND_API_ENDPOINT_FOR_RECOMMENDED_PRODUCTS');
      const data = await response.json();
      setRecommendedProducts(data);
    } catch (error) {
      console.error('Error fetching recommended products:', error);
    }
  };

  const fetchProductListings = async () => {
    try {
      // Make API call to fetch product listings
      const response = await fetch('YOUR_BACKEND_API_ENDPOINT_FOR_PRODUCT_LISTINGS');
      const data = await response.json();
      setProductListings(data);
    } catch (error) {
      console.error('Error fetching product listings:', error);
    }
  };

  return (
    <View style={styles.container}>
      {/* Include the Header component */}
      <Header style={styles.header} />

      {/* Include the CategorySlider component */}
      <CategorySlider style={styles.categorySlider} />

      {/* Include the Banner component with banner text */}
      <Banner text="Welcome to Our Store! Explore our wide range of products."/>

      {/* Special deals section */}
      <View style={styles.section}>
        <Text style={styles.sectionHeading}>Special Deals</Text>
        {specialDeals.map((deal) => (
          <Text key={deal.id}>{deal.name}</Text>
        ))}
      </View>

      {/* Recommended products section */}
      <View style={styles.section}>
        <Text style={styles.sectionHeading}>Recommended Products</Text>
        {recommendedProducts.map((product) => (
          <Text key={product.id}>{product.name}</Text>
        ))}
      </View>

      {/* Product listings section */}
      <View style={styles.section}>
        <Text style={styles.sectionHeading}>Product Listings</Text>
        {productListings.map((product) => (
          <Text key={product.id}>{product.name}</Text>
        ))}
      </View>

      {/* Include the Footer component */}
      <Footer />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column', // Vertical layout
    justifyContent: 'space-between',
  },
  header: {
    marginBottom: 0, // Adjust the margin bottom of the header here
  },
  categorySlider: {
    // Adjust the styles of the CategorySlider component here
    padding: 10,
  },
  section: {
    marginBottom: 20,
  },
  sectionHeading: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
});

export default Home;
