import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView, FlatList } from 'react-native';

const Menu = () => {
  const [showCategories, setShowCategories] = useState(false);

  // Function to handle navigation to different screens
  const handleNavigation = (screenName) => {
    // Add navigation logic here
    console.log(`Navigating to ${screenName}`);
  };

  // Function to toggle the visibility of categories
  const toggleCategories = () => {
    setShowCategories(!showCategories);
  };

  // Dummy data for categories
  const categoriesData = [
    { id: 1, name: 'Automobile' },
    { id: 2, name: 'Women Fashion' },
    { id: 3, name: 'Men Fashion' },
    { id: 4, name: 'Kids Fashion' },
    { id: 5, name: 'Electrical & Electronics' },
    { id: 6, name: 'Sports, Fitness & Outdoor' },
    { id: 7, name: 'Computers & Gaming' },
    { id: 8, name: 'Books, Media & Music' },
    { id: 9, name: 'Daily Deals' },
    { id: 10, name: 'Home & Kitchen' },
    { id: 11, name: 'Mobile & Tablets' },
    { id: 12, name: "Kids' Toys" },
  ];

  // Render category item
  const renderCategoryItem = ({ item }) => (
    <TouchableOpacity style={styles.categoryButton} onPress={() => handleNavigation(item.name)}>
      <Text>{item.name}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Menu</Text>
      <TouchableOpacity style={styles.button} onPress={toggleCategories}>
        <Text>Shop by Categories</Text>
      </TouchableOpacity>
      {/* Render categories only if showCategories is true */}
      {showCategories && (
        <View style={styles.categories}>
          <FlatList
            data={categoriesData}
            renderItem={renderCategoryItem}
            keyExtractor={(item) => item.id.toString()}
            numColumns={2}
          />
        </View>
      )}
      {/* End of categories */}
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('DealsOffers')}>
        <Text>Deals/Offers</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('AllVendors')}>
        <Text>All Vendors</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('Review')}>
        <Text>Review</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('Feedback')}>
        <Text>Feedback</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#DDDDDD',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
  },
  categories: {
    width: '100%',
    marginTop: 10,
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  categoryButton: {
    backgroundColor: '#CCCCCC',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
    width: '48%', // Adjust the width based on the number of columns
  },
});

export default Menu;
