import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';

const Menu = () => {
  const [showCategories, setShowCategories] = useState(false);

  // Function to handle navigation to different screens
  const handleNavigation = (screenName) => {
    // Add navigation logic here
    console.log(`Navigating to ${screenName}`);
  };

  // Function to toggle the visibility of categories
  const toggleCategories = () => {
    setShowCategories(!showCategories);
  };

  return (
    <ScrollView contentContainerStyle={styles.scrollViewContent}>
      <View style={styles.container}>
        <Text style={styles.title}>Menu</Text>
        <TouchableOpacity style={styles.button} onPress={toggleCategories}>
          <Text>Shop by Categories</Text>
        </TouchableOpacity>
        {/* Render categories only if showCategories is true */}
        {showCategories && (
          <View style={styles.categories}>
            <TouchableOpacity style={styles.categoryButton} onPress={() => handleNavigation('Automobile')}>
              <Text>Automobile</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.categoryButton} onPress={() => handleNavigation('WomenFashion')}>
              <Text>Women Fashion</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.categoryButton} onPress={() => handleNavigation('MenFashion')}>
              <Text>Men Fashion</Text>
            </TouchableOpacity>
            {/* Add more categories as needed */}
          </View>
        )}
        {/* End of categories */}
        <TouchableOpacity style={styles.button} onPress={() => handleNavigation('DealsOffers')}>
          <Text>Deals/Offers</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleNavigation('AllVendors')}>
          <Text>All Vendors</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleNavigation('Review')}>
          <Text>Review</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => handleNavigation('Feedback')}>
          <Text>Feedback</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollViewContent: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#DDDDDD',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
  },
  categories: {
    width: '100%',
    marginTop: 10,
  },
  categoryButton: {
    backgroundColor: '#CCCCCC',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
  },
});

export default Menu;
