import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, Image, TouchableOpacity } from 'react-native';

const SpecialDeals = () => {
  const [deals, setDeals] = useState([]);
  const [loading, setLoading] = useState(true); // State to manage loading state

  useEffect(() => {
    fetchSpecialDeals();
  }, []);

  const fetchSpecialDeals = async () => {
    try {
      // Make API call to fetch special deals
      const response = await fetch('YOUR_BACKEND_API_ENDPOINT_FOR_SPECIAL_DEALS');
      const data = await response.json();
      setDeals(data);
      setLoading(false); // Set loading to false when data is fetched successfully
    } catch (error) {
      console.error('Error fetching special deals:', error);
      setLoading(false); // Set loading to false even if there's an error
    }
  };

  const renderLoading = () => (
    <View style={styles.loadingContainer}>
      <Text>Loading...</Text>
    </View>
  );

  const renderMockData = () => {
    const mockDeals = [
      { id: '1', name: 'Mock Deal 1', imageUrl: 'https://www.google.com/imgres?imgurl=https%3A%2F%2Fimgresizers.com%2Fidea.svg&tbnid=RXbh9QBcAvWnKM&vet=12ahUKEwio_NTmm-WEAxU2oGMGHfW0AJYQMygAegQIARBK..i&imgrefurl=https%3A%2F%2Fimgresizers.com%2Fimage-to-150x150&docid=gBFzP_cNugW_JM&w=512&h=512&q=some%20image%20that%20need%20to%20be%20in%20150%20px&ved=2ahUKEwio_NTmm-WEAxU2oGMGHfW0AJYQMygAegQIARBK', discount: 10 },
      { id: '2', name: 'Mock Deal 2', imageUrl: 'https://via.placeholder.com/150', discount: 15 },
      { id: '3', name: 'Mock Deal 3', imageUrl: 'https://via.placeholder.com/150', discount: 20 },
      { id: '4', name: 'Mock Deal 4', imageUrl: 'https://via.placeholder.com/150', discount: 25 },
    ];

    return (
      <FlatList
        data={mockDeals}
        renderItem={renderItem}
        keyExtractor={item => item.id}
        horizontal
        showsHorizontalScrollIndicator={false}
      />
    );
  };

  const renderItem = ({ item }) => (
    <TouchableOpacity style={styles.itemContainer}>
      <Image source={{ uri: item.imageUrl }} style={styles.image} />
      <Text style={styles.title}>{item.name}</Text>
      <Text style={styles.discount}>{item.discount}% OFF</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Special Deals</Text>
      {loading ? renderLoading() : renderMockData()}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginBottom: 20,
  },
  heading: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
    marginLeft: 10,
  },
  itemContainer: {
    marginHorizontal: 10,
  },
  image: {
    width: 150,
    height: 150,
    borderRadius: 10,
  },
  title: {
    fontSize: 16,
    marginTop: 5,
  },
  discount: {
    fontSize: 14,
    fontWeight: 'bold',
    color: 'red',
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default SpecialDeals;
