import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

const Menu = () => {
  // Function to handle navigation to different screens
  const handleNavigation = (screenName) => {
    // Add navigation logic here
    console.log(`Navigating to ${screenName}`);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Menu</Text>
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('ShopByCategories')}>
        <Text>Shop by Categories</Text>
      </TouchableOpacity>
      {/* Subcategories under Shop by Categories */}
      <View style={styles.subcategories}>
        <TouchableOpacity style={styles.subcategoryButton} onPress={() => handleNavigation('Automobile')}>
          <Text>Automobile</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.subcategoryButton} onPress={() => handleNavigation('WomenFashion')}>
          <Text>Women Fashion</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.subcategoryButton} onPress={() => handleNavigation('MenFashion')}>
          <Text>Men Fashion</Text>
        </TouchableOpacity>
        {/* Add more subcategories as needed */}
      </View>
      {/* End of subcategories */}
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('DealsOffers')}>
        <Text>Deals/Offers</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('AllVendors')}>
        <Text>All Vendors</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('Review')}>
        <Text>Review</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => handleNavigation('Feedback')}>
        <Text>Feedback</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#DDDDDD',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
  },
  subcategories: {
    width: '100%',
    marginTop: 10,
  },
  subcategoryButton: {
    backgroundColor: '#CCCCCC',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
  },
});

export default Menu;
