// CategorySlider.js
import React from 'react';
import { View, ScrollView, StyleSheet, Text, TouchableOpacity } from 'react-native';

const categories = [
  'AUTOMOBILE', 'WOMEN FASHION', 'MEN FASHION', 'ELECTRICAL & ELECTRONICS',
  'SPORTS, FITNESS & OUTDOOR', 'COMPUTERS & GAMING', 'BOOKS, MEDIA & MUSIC',
  'DAILY NEEDS', 'HOME & KITCHEN', 'MOBILE & TABLETS', 'KIDS\' TOYS & FASHION',
  'INDUSTRIAL MACHINERY & EQUIPMENT', 'AGRICULTURAL PRODUCTS', 'HANDICRAFTS'
];

const categoryslider = () => {
  return (
    <ScrollView horizontal showsHorizontalScrollIndicator={false}>
      {categories.map((category, index) => (
        <TouchableOpacity key={index} style={styles.category}>
          <Text style={styles.categoryText}>{category}</Text>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  category: {
    backgroundColor: '#f0f0f0',
    paddingVertical: 10,
    paddingHorizontal: 20,
    marginHorizontal: 5,
    borderRadius: 20,
  },
  categoryText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
  },
});

